// Combined JavaScript file
// Include jQuery first
document.write('<script src="https://code.jquery.com/jquery-3.6.0.min.js"><\/script>');

// Include other scripts after jQuery is loaded
window.addEventListener('load', function() {
    // Remove the preloader once everything is loaded
    const preloader = document.querySelector('.spinner-wrapper');
    if (preloader) {
        preloader.style.display = 'none';
    }

    // Load other scripts in order
    const scripts = [
        'popper.min.js',
        'bootstrap.min.js',
        'jquery.easing.min.js',
        'jquery.countdown.min.js',
        'swiper.min.js',
        'jquery.magnific-popup.js',
        'validator.min.js',
        'scripts.js'
    ];
    
    let loadedScripts = 0;
    scripts.forEach(function(src) {
        const script = document.createElement('script');
        script.src = 'js/' + src;
        script.onload = function() {
            loadedScripts++;
            if (loadedScripts === scripts.length) {
                // Initialize any required plugins here
                if (typeof Swiper !== 'undefined') {
                    // Initialize Swiper sliders
                    new Swiper('.image-slider-1', {
                        navigation: {
                            nextEl: '.swiper-button-next',
                            prevEl: '.swiper-button-prev',
                        }
                    });
                    new Swiper('.image-slider-2', {
                        autoplay: {
                            delay: 2000,
                            disableOnInteraction: false
                        },
                        loop: true,
                        spaceBetween: 30,
                        slidesPerView: 5,
                        breakpoints: {
                            580: { slidesPerView: 1 },
                            768: { slidesPerView: 2 },
                            992: { slidesPerView: 3 },
                            1200: { slidesPerView: 4 }
                        }
                    });
                }
            }
        };
        document.body.appendChild(script);
    });
}); 